
public class PrintToTheConsole {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	      String tekijä = "Kalle";
	      double luku1 = 11;
	      double luku2 = 22;
	      double tulo, summa, erotus, osamäärä;
	      
	      luku1 = luku1 = 5;
	      luku2 = luku2 = 2;
	      tulo = luku1 * luku2;
	      summa = luku1 + luku2;
	      erotus = luku1 - luku2;
	      osamäärä = luku1 / luku2;
	      
	      
	      System.out.println("Hei olen Tulostin-ohjelma");
	      System.out.println("Ohjelman tekijä:" + tekijä);
	      System.out.println("Luku2-muuttujan arvo on:" + luku2);
	      
	      System.out.println("Luku1-muuttujan arvo on:" + luku1);
	      
	      System.out.println("Luku1 ja luku2 tulo on:" + luku1 + "*" + luku2 + "=" + tulo);
	      
	      System.out.println("Luku1 ja luku2 summa on:" + luku1 + "+" + luku2 + "=" + summa);
	      
	      System.out.println("Luku1 ja luku2 erotus on:" + luku1 + "-" + luku2 + "=" + erotus);
	      
	      System.out.println("Luku1 ja luku2 osamäärä on:" + luku1 + "/" + luku2 + "=" + osamäärä);
		
	}

}
